exports.parse = function(defaults) {
    var opts = {};
    Object.keys(defaults).forEach(function(key) {
        opts[key] = defaults[key];
    });
    var lastOpt = null;
    process.argv.forEach(function(opt) {
        if (opt.indexOf('-') == 0) {
            lastOpt = opt.substr(1);
            opts[lastOpt] = true;
        } else if (lastOpt) {
            opts[lastOpt] = opt;
            lastOpt = null;
        }
    });
    return opts;
};